<script>
import axios from 'axios'
import { onMounted, ref } from 'vue'

export default {
    name: 'IndexProduct',
    
    setup() {

        //reactive state
        let bayi = ref([])
        let pelajar = ref([])
        let personal = ref([])
        let bisnis = ref([])

        //mounted
        onMounted(() => {

            //get API from Laravel Backend
            axios.get('http://localhost:8000/api/product')
            .then(response => {
              
              //assign state posts with response data
              bayi.value = response.data.data[3]
              pelajar.value = response.data.data[2]
              personal.value = response.data.data[1]
              bisnis.value = response.data.data[0]
            
            }).catch(error => {
                console.log(error.response.data)
            })

        })

        //return
        return {
            bayi,
            pelajar,
            personal,
            bisnis
        }
    }
}
</script>

